package com.tnsif.loopsdemo;

public class DoWhileLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=11;
		do 
		{
			System.out.println(i);
			i++;
		}
		while(i<=10);
		
		int i1=6;
		do 
		{
			System.out.println(i1);
			i1++;
		}
		while(i1<=10);
	}

}
